/// <reference types="node" />
import { FastifyInstance, FastifyReply, FastifyRequest } from 'fastify';
import * as http from 'http';
import * as http2 from 'http2';
import { Principal } from './auth/Principal';
export declare const types: {
    AuthProvider: string;
    Controller: string;
    Healthcheck: string;
    Logger: string;
    MongoService: string;
};
export declare type HttpRequest = http.IncomingMessage | http2.Http2ServerRequest;
export declare type HttpResponse = http.ServerResponse | http2.Http2ServerResponse;
export declare type HttpServer = http.Server | http2.Http2Server;
